mapper.select:
select `month`, SUM(gross_profit) as totalGrossProfit, ABS(SUM(advertising_fee))/SUM(sales_revenue) as advertisingFeeRate, ABS(SUM(advertising_fee)) as totalAdvertisingFee, SUM(sales_revenue) as totalSalesRevenue, ABS(SUM(compensation)) as totalCompensation, ABS(SUM(compensation))/SUM(sales_revenue) as compensationRate, ABS(SUM(final_freight)) as totalFinalFreight, ABS(SUM(final_freight))/SUM(sales_revenue) as finalFreightRate, ABS(SUM(packaging_fee)) as totalPackagingFee, ABS(SUM(packaging_fee))/SUM(sales_revenue) as packagingRage, ABS(SUM(promotion_fee)) as totalPromotionFee, ABS(SUM(promotion_fee))/SUM(sales_revenue) as promotionFeeRate, SUM(platform_refund) as totalPlatformRefund, SUM(platform_refund)/SUM(sales_revenue) as platformRefundRate, ABS(SUM(final_freight_return)) as totalFinalFreightReturn, ABS(SUM(final_freight_return))/SUM(sales_revenue) as finalFreightReturn, ABS(SUM(other_transaction_fee)) as totalOtherTransactionFee, ABS(SUM(other_transaction_fee))/SUM(sales_revenue) as otherTransactionFeeRate, ABS(SUM(selfdelivery_commission)) as totalSelfdeliveryCommission, ABS(SUM(selfdelivery_commission))/SUM(sales_revenue) as selfdeliveryCommissionRate, ABS(SUM(fba_sales_commission)) as totalFbaSalesCommission, ABS(SUM(fba_sales_commission))/SUM(sales_revenue) as fbaSalesCommissionRate, ABS(SUM(final_freight_return)) as totalFinalFreightReturn, ABS(SUM(final_freight_return))/SUM(sales_revenue) as finalFreightReturnRate, ABS(SUM(final_freight_return)) as totalFinalFreightReturn2, ABS(SUM(final_freight_return))/SUM(sales_revenue) as finalFreightReturn2Rate, ABS(SUM(storage_fee)) as totalStorageFee, ABS(SUM(storage_fee))/SUM(sales_revenue) as storageFeeRate, ABS(SUM(shipping_label_fee)) as totalShippingLabelFee, ABS(SUM(shipping_label_fee))/SUM(sales_revenue) as shippingLabelFeeRate, ABS(SUM(platform_service_fee)) as totalPlaformServiceFee, ABS(SUM(platform_service_fee))/SUM(sales_revenue) as plaformServiceFeeRate, ABS(SUM(platform_refund_service_fee)) as totalPlatformRefundServiceFee, ABS(SUM(platform_refund_service_fee))/SUM(sales_revenue) as platformRefundServiceFeeRate, ABS(SUM(platform_service_adjustment_fee)) as totalPlatformServiceAdjustmentFee, ABS(SUM(platform_service_adjustment_fee))/SUM(sales_revenue) as platformServiceAdjustmentFeeRate, ABS(SUM(service_provider_fee)) as totalServiceProviderFee, ABS(SUM(service_provider_fee))/SUM(sales_revenue) as serviceProviderFeeRate, ABS(SUM(other_fee)) as totalOtherFee, ABS(SUM(other_fee))/SUM(sales_revenue) as otherFeeRate, SUM(mixed_vat) as tatalMixvat, SUM(mixed_vat)/SUM(sales_revenue) as mixedVatRate, SUM(mixed_vat2) as tatalMixvat2, SUM(mixed_vat2)/SUM(sales_revenue) as mixedVatRate2, SUM(clear_broker_fee) as tatalClearBrokerFee, SUM(clear_broker_fee)/SUM(sales_revenue) as clearBrokerFeeRate, SUM(asinking_num) as totalAsinkingNum, SUM(yc_delivery_num) as tatalYcDeliveryNum, SUM(sales_revenue)/SUM(asinking_num) as price from cpd_finance
and month = #{month} and site = #{site} and principal = #{principal} and type = #{type}
GROUP BY `month`

