mapper.select:
SELECT principal,type,spu,standard_sku,site,account, sum(product_sales) as product_sales, SUM(product_sales_tax) as product_sales_tax, SUM(postage_credits) as postage_credits, SUM(shipping_credits_tax) as shipping_credits_tax, SUM(gift_wrap_credits) as gift_wrap_credits, SUM(giftwrap_credits_tax) as giftwrap_credits_tax, SUM(promotional_rebates) as promotional_rebates, sum(promotional_rebates_tax) as promotional_rebates_tax, SUM(marketplace_withheld_tax) as marketplace_withheld_tax, SUM(selling_fees) as selling_fees, SUM(fba_fees) as fba_fees, sum(other_transaction_fees) as other_transaction_fees, SUM(other) as other, SUM(total) as total from oms_transaction_record
and time >= #{params.beginTime} and time <= #{params.endTime} and month = #{month} and type = #{type} and fulfilment = #{fulfilment}
and site = #{site} and spare_field = #{spareField}
GROUP BY principal,type,spu,standard_sku,site,account

SELECT principal,spu,standard_sku,site,account,
sum(quantity) as quantity
from oms_transaction_record

     and month = #{month}
     and time >= #{params.beginTime}
     and time <= #{params.endTime}
     and site = #{site}
     and spare_field = #{spareField}
      and type = #{type}

GROUP BY principal,spu,standard_sku,site,account

SELECT sum(quantity) as quantity,
       sum(product_sales) as productSales,
       sum(other_transaction_fees) as otherTransactionFees,
       sum(other) as other
       from oms_transaction_record

    and month = #{month}
     and time >= #{params.beginTime}
     and time <= #{params.endTime}
     and type = #{type}
    and description = #{description}
     and fulfilment = #{fulfilment}
     and fulfilment = #{fulfilment}
     and site = #{site}
     and spare_field = #{spareField}

SELECT
rd.principal,rd.spu,rd.standard_sku as standardSku,sum(rd.removal_fee) as total
FROM
(SELECT DISTINCT order_id from oms_transaction_record

    and month = #{month}
     and time >= #{params.beginTime}
     and time <= #{params.endTime}
    and description = #{description}
     and fulfilment = #{fulfilment}
     and fulfilment = #{fulfilment}
     and site = #{site}
     and spare_field = #{spareField}

) tr
INNER JOIN oms_removal_detail rd ON tr.order_id = rd.order_id

    and rd.order_type = #{params.removalType}

GROUP BY rd.principal,rd.spu,rd.standard_sku

select tr.account,tr.site,tr.principal,tr.spu,tr.standard_sku as standardSku,SUM(rs.amount) as total from
(select distinct order_id,sku,account,site,principal,spu,standard_sku from oms_transaction_record
where type=5
and month = #{month}
 and time >= #{params.beginTime}
 and time <= #{params.endTime}
 and type = #{type}
and description = #{description}
 and fulfilment = #{fulfilment}
 and fulfilment = #{fulfilment}
 and site = #{site}
 and spare_field = #{spareField})  tr
INNER JOIN oms_refund_servicefee rs on tr.order_id=rs.order_id and tr.sku=rs.sku
GROUP BY tr.account,tr.site,tr.principal,tr.spu,tr.standard_sku

select account,site,principal,spu,standard_sku as standardSku,SUM(other) as other from oms_transaction_record

    and month = #{month}
     and time >= #{params.beginTime}
     and time <= #{params.endTime}
     and type = #{type}
    and description = #{description}
     and fulfilment = #{fulfilment}
     and fulfilment = #{fulfilment}
     and site = #{site}
     and spare_field = #{spareField}

GROUP BY account,site,principal,spu,standard_sku

select standard_sku as standardSku,spu,principal,site,account,SUM(other_transaction_fees) as otherTransactionFees from oms_transaction_record

    and month = #{month}
     and time >= #{params.beginTime}
     and time <= #{params.endTime}
     and type = #{type}
     and description like #{description}
     and fulfilment = #{fulfilment}
     and fulfilment = #{fulfilment}
     and site = #{site}
     and spare_field = #{spareField}

GROUP BY standard_sku,spu,principal,site,account

select pr.sku,SUM(tr.other_transaction_fees) as fee from oms_transaction_record tr INNER JOIN oms_dealfee_asin da on tr.id=da.record_id
INNER JOIN pms_productinfo_relation pr on pr.asin=da.asin

    and month = #{month}
     and tr.time >= #{params.beginTime}
     and tr.time <= #{params.endTime}
     and tr.sku = #{sku}
     and tr.type = #{type}
     and tr.description like #{description}
     and tr.fulfilment = #{fulfilment}
     and tr.fulfilment = #{fulfilment}
     and tr.site = #{site}
     and tr.spare_field = #{spareField}

GROUP BY pr.sku

select tr.id, tr.account, tr.site, tr.time, tr.settlement_id, tr.type, tr.order_id, tr.sku, tr.description, tr.quantity, tr.marketplace,
tr.fulfilment, tr.order_city,tr.order_state, tr.order_postal, tr.tax_collection_model, tr.product_sales, tr.product_sales_tax,
tr.postage_credits, tr.shipping_credits_tax,tr.gift_wrap_credits, tr.giftwrap_credits_tax, tr.promotional_rebates,
tr.promotional_rebates_tax, tr.marketplace_withheld_tax,tr.selling_fees, tr.fba_fees,tr.other_transaction_fees, tr.other, tr.total,
tr.standard_sku, tr.spu, tr.principal, tr.charg_type, tr.spare_field, tr.create_by, tr.create_time, tr.update_by,tr.update_time, da.asin
from oms_transaction_record tr LEFT JOIN oms_dealfee_asin da on tr.description=da.description

     and tr.account = #{account}
     and tr.site = #{site}
    and month = #{month}
     and tr.time = #{time}
     and tr.settlement_id = #{settlementId}
     and tr.type = #{type}
     and tr.order_id = #{orderId}
     and tr.sku = #{sku}
     and tr.description = #{description}
     and tr.description like #{description}
     and tr.quantity = #{quantity}
     and tr.marketplace = #{marketplace}
     and tr.fulfilment = #{fulfilment}
     and tr.order_city = #{orderCity}
     and tr.order_state = #{orderState}
     and tr.order_postal = #{orderPostal}
     and tr.tax_collection_model = #{taxCollectionModel}
     and tr.product_sales = #{productSales}
     and tr.product_sales_tax = #{productSalesTax}
     and tr.postage_credits = #{postageCredits}
     and tr.shipping_credits_tax = #{shippingCreditsTax}
     and tr.gift_wrap_credits = #{giftWrapCredits}
     and tr.giftwrap_credits_tax = #{giftwrapCreditsTax}
     and tr.promotional_rebates = #{promotionalRebates}
     and tr.promotional_rebates_tax = #{promotionalRebatesTax}
     and tr.marketplace_withheld_tax = #{marketplaceWithheldTax}
     and tr.selling_fees = #{sellingFees}
     and tr.fba_fees = #{fbaFees}
     and tr.other_transaction_fees = #{otherTransactionFees}
     and tr.other = #{other}
     and tr.total = #{total}
     and tr.standard_sku = #{standardSku}
     and tr.spu = #{spu}
     and tr.principal = #{principal}
     and tr.charg_type = #{chargType}
     and tr.spare_field = #{spareField}
     and da.asin = #{asin}

order by id desc
